import Vue from 'vue'
import Component from 'vue-class-component'
import { Provide } from 'vue-property-decorator'
import { NavList } from '../../utils/models/interface'

@Component({

})
export default class Nav extends Vue {
  index: Number = 0;
  firstNavList: Array<NavList> = [
    {
      value: '关于QG',
      label: 'about',
      children: [
        {
          value: '指导老师',
          label: '',
          children: [],
          isActive: false
        },
        {
          value: '研究方向',
          label: '',
          children: [],
          isActive: false
        },
        {
          value: '最近的荣誉',
          label: '',
          children: [],
          isActive: false
        },
      ],
      isActive: true
    },
    {
      value: '我们',
      label: 'we',
      children: [],
      isActive: false
    },
    {
      value: '成员',
      label: 'members',
      children: [],
      isActive: false
    },
    {
      value: '项目',
      label: 'projects',
      children: [],
      isActive: false
    },
    {
      value: '荣誉',
      label: 'honors',
      children: [],
      isActive: false
    },
    {
      value: '专栏',
      label: 'column',
      children: [],
      isActive: false
    },
  ];
  secondNavList: Array<NavList> = []

  switchFirstNav(event: any) {
    let target: any = event.target,
        index = target.getAttribute('data-index');
    if (!index) {
      // 点击到父容器
      return ;
    }
    for (let i = 0; i < this.$data.firstNavList.length; i++) {
      this.$data.firstNavList[i].isActive = false;
    }
    this.$router.replace({
      name: this.$data.firstNavList[index].label
    })
  }
}
